function [gamInverse,dacsize] = LoadCalibrationFileVL(calibrationFile)% LoadCalibrationFile.m is a function that loads and returns gamInverse & dacsize from the calibrationFile% created by FT on 2000/05/10 and modified by JJ on 2007/06/28global expdirerr = 0;cd([expdir filesep 'Local_Functions'])eval(['load ' calibrationFile ' gamInverse dacsize'],'err = 1');		% load gamInverse & dacsize from CalibrationFileif err	disp(['Warning! Calibration File Not Found!']);	query = input('Do you want to proceed using uncorrected gamma?  y or n(default) ??  ','s');	if ~isempty(query) & query(1) == 'y'		gamInverse = [0:255; 0:255; 0:255]';	% uncorrected gamma inverse function		dacsize = 8;	else		error('Exiting program...');	endendcd(expdir);